package com.fiap.fastfood.external.orm;

import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBAttribute;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBAutoGeneratedKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBHashKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBTable;
import com.fiap.fastfood.core.entity.OrderPaymentStatus;
import com.fiap.fastfood.core.entity.OrderStatus;
import lombok.*;
import org.springframework.data.annotation.CreatedDate;
import org.springframework.data.annotation.Id;
import org.springframework.data.annotation.LastModifiedDate;

import java.math.BigDecimal;
import java.time.LocalDateTime;

@Setter
@Builder
@NoArgsConstructor
@AllArgsConstructor
@DynamoDBTable(tableName = "Order")
public class OrderORM {

    @Id
    private String id;
    private String customerId;
//    private List<ItemORM> items;
    private BigDecimal totalValue;
    private OrderStatus status;
    private OrderPaymentStatus paymentStatus;
    @CreatedDate
    private LocalDateTime createdAt;
    @LastModifiedDate
    private LocalDateTime updatedAt;

    @DynamoDBHashKey
    @DynamoDBAutoGeneratedKey
    public String getId() {
        return id;
    }

    @DynamoDBAttribute
    public String getCustomerId() {
        return customerId;
    }

    @DynamoDBAttribute
    public BigDecimal getTotalValue() {
        return totalValue;
    }

    @DynamoDBAttribute
    public OrderStatus getStatus() {
        return status;
    }

    @DynamoDBAttribute
    public OrderPaymentStatus getPaymentStatus() {
        return paymentStatus;
    }

    @DynamoDBAttribute
    public LocalDateTime getCreatedAt() {
        return createdAt;
    }

    @DynamoDBAttribute
    public LocalDateTime getUpdatedAt() {
        return updatedAt;
    }
}
