package com.fiap.fastfood.external.orm;

import com.amazonaws.services.dynamodbv2.datamodeling.*;
import com.fiap.fastfood.common.utils.Converter;
import com.fiap.fastfood.core.entity.OrderPaymentStatus;
import com.fiap.fastfood.core.entity.OrderStatus;
import lombok.AllArgsConstructor;
import lombok.Builder;
import lombok.NoArgsConstructor;
import lombok.Setter;
import org.springframework.data.annotation.CreatedDate;
import org.springframework.data.annotation.Id;
import org.springframework.data.annotation.LastModifiedDate;

import java.math.BigDecimal;
import java.time.LocalDateTime;
import java.util.List;

@Setter
@Builder
@NoArgsConstructor
@AllArgsConstructor
@DynamoDBTable(tableName = "Order")
public class OrderORM {

    private String id;
    private Long customerId;
    private List<ItemORM> items;
    private BigDecimal totalValue;
    private String status;
    private String paymentStatus;
    @CreatedDate
    private LocalDateTime createdAt;
    @LastModifiedDate
    private LocalDateTime updatedAt;

    @DynamoDBHashKey
    @DynamoDBAutoGeneratedKey
    public String getId() {
        return id;
    }

    @DynamoDBAttribute
    public Long getCustomerId() {
        return customerId;
    }

    @DynamoDBAttribute
    public BigDecimal getTotalValue() {
        return totalValue;
    }

    @DynamoDBAttribute
    public String getStatus() {
        return status;
    }

    @DynamoDBAttribute
    public String getPaymentStatus() {
        return paymentStatus;
    }

    @DynamoDBTypeConverted(converter = Converter.LocalDateTimeConverter.class )
    public LocalDateTime getCreatedAt() {
        return createdAt;
    }

    @DynamoDBTypeConverted(converter = Converter.LocalDateTimeConverter.class )
    public LocalDateTime getUpdatedAt() {
        return updatedAt;
    }

    @DynamoDBAttribute
    public List<ItemORM> getItems() {
        return items;
    }
}
